class Solution {
public:
    int findSmallestInteger(vector<int>& nums, int val) {

        //make positive
        for(int i=0;i<nums.size();i++){
            if(nums[i]<0){
                if(abs(nums[i])%val==0){
                    int quo=abs(nums[i])/val;
                    nums[i]+=(val*quo);
                }else{
                    int quo=abs(nums[i])/val;
                    quo+=1;
                    nums[i]+=(val*quo);
                }
            }
        }

        //sort, hash
        map<int,queue<int>> mp;
        sort(nums.begin(),nums.end());
        for(int i=0;i<nums.size();i++){
            int rem=nums[i]%val;
            mp[rem].push(nums[i]);
        }
        //for(auto it:nums) cout<<it<<" ";
        //find
        int c=0;
        for(int i=0;i<nums.size();i++){
