```
class Solution {
public:
    int findSmallestInteger(vector<int>& nums, int value) {

        int n = nums.size();

        // declare an unordered map

        unordered_map<int, int> mp;

        // store the frequency of the mod

        for(int i = 0; i < n; i++)
        {
            int mod = nums[i] % value;

            if(mod < 0)
            {
                mod += value;
            }

            mp[mod]++;
        }

        // declare a visited array

        vector<bool> vis(1e6 + 1, false);

        // traverse over the map and mark the possible integer as visited

        for(auto x : mp)
        {
            for(int i = 0; i < x.second; i++)
            {
                vis[x.first + i * value] = true;
            }
        }

        // find the first missing non-negative number

        for(int i = 0; i < vis.size(); i++)
        {
            if(vis[i] == false)
            {
                return i;
            }
        }

        return 1e6 + 1;
    }
};
```

