class Solution {
public:
    int findSmallestInteger(vector<int>& nums, int value) {
        int n=nums.size();
        vector<int>v(value,0);
        int afterComput;
        for( int i=0 ; i < n; i++ )
        {
            if(nums[i] >= 0)
            {
                afterComput = nums[i] % value;
            }
            else
            {
                if( (abs(nums[i]) % value ) == 0)
                {
                    afterComput = 0;
                }
                else
                {
                    afterComput = value - (abs(nums[i]) % value);
                }
            }
            v[afterComput]++;
        }
        int ans=INT_MAX;
        for(int i = 0; i < v.size(); i++) {
            if(v[i] == 0) return i;
            ans = min(ans, value*v[i] + i);
        }
        return ans;
    }
};
