	}
	minSize, minValue := math.MaxInt64, math.MaxInt64
	for i := 0; i < value; i++ {
		if magic[i] == 0 {
			return i
		}
		if magic[i] < minSize {
			minSize, minValue = magic[i], i
		}
	}
	return value * minSize + minValue
}
