class Solution:
    def findSmallestInteger(self, nums: List[int], value: int) -> int:
        '''
        two cases i have to consider:
        one - multiple of same number


        '''
        if value == 1:
            return len(nums)


        for i, num in enumerate(nums):
            nums[i] %= value

        seen = defaultdict(int)

        nums.sort()

        for i in range(len(nums)):

            if not seen[nums[i]]:
                seen[nums[i]] += 1
                continue

            l, r = 1, 10 ** 5

            while l < r:
                mid = (l + r) // 2

                if seen[nums[i] + value * mid]:
                    l = mid + 1

                else:
                    r = mid

            seen[nums[i] + value * l] += 1




        nums.sort()

        for i in range(len(nums)):
            if not seen[i]:
                return i

        return len(nums)
