func findSmallestInteger(nums []int, value int) int {
  candidates := make([]int, value)
  for i := 0; i < len(nums); i++ {
    // We're doing (num % val + val)%val to handle negative numbers.
    // num%val gives the closest number <= 0. Then we add value and then take mod again.
    // -7 % 4 => -3 => (-3 + 4)%4 => 1
    candidates[(nums[i]%value+value)%value]++
  }
  for i := 0; i < len(nums); i++ {
    if candidates[i%value] == 0 {
      return i
    }
    candidates[i%value]--
  }
  return len(nums)
}

func abs(a int) int {
  if a < 0 {
    return -a
  }
  return a
}
```

