class Solution {
public:
    int findSmallestInteger(vector<int>& nums, int value) {
        vector<int> dp(value,0);
        int n = nums.size();

        for(auto i =0 ; i< n ;i++){
            if(nums[i]>=0)
                dp[nums[i]%value]++;
            else{
                int c=-1*nums[i];
                c= (value-(c%value))%value;
                dp[c]++;
            }
        }
        int index =0;
        for(int i =1; i< value ; i++){
            if(dp[i]<dp[index]){
                index=i;
            }
        }
        return (value*dp[index])+index;
    }
};
