class Solution {
public:
    int findSmallestInteger(vector<int>& nums, int value) {
        // counts of remainders divided by value
        vector<int> counts(value, 0);
        for (int i = 0; i < nums.size(); i ++){
            int remainder = nums[i] % value;
            if (remainder < 0){
                remainder += value;
            }
            counts[remainder]++;
        }
        int place = min_element(counts.begin(), counts.end()) - counts.begin();
        int count = counts[place];
        return count * value + place;
    }
};
```

