class Solution {
public:
    int findSmallestInteger(vector<int>& nums, int value) {
        int n = nums.size();
        // sort(nums.begin(),nums.end());
        int range = nums[n-1];
        vector<int> vis(value,0);
        for(int i=0; i<nums.size(); i++){
            int ind = (nums[i]%value + value)%value;
            vis[ind]++;
        }
        int mn = 1e9;
        int val = -1;
        for(int i=0; i<value; i++){
            if(vis[i]<mn){
                mn = vis[i];
                val = i;
            }
            else if(vis[i]==mn){
                val = min(val,i);
            }
        }
        return value*mn + val;



    }
};
```

