class Solution {
public:
    // This function calculates the modulo of the dividend and divisor,
    // and returns a positive result if the modulo is negative.
    int moduloCalculator(int dividend, int divisor) {
        int r = dividend % divisor;
        if (r < 0) {
            return r + divisor;
        } else {
            return r;
        }
    }

    int findSmallestInteger(vector<int>& nums, int value) {
        int arrSize = nums.size();
        map<int, int> m;

        // Iterate over the array, and add each element to the map with its modulo value as the key.
        for (int i = 0; i < arrSize; i++) {
            if (nums[i] < 0) {
                m[moduloCalculator(nums[i], value)]++;
            } else {
                m[nums[i] % value]++;
            }
        }

        // Iterate over the range of integers from 0 to arrSize-1,
        // and check if each integer is in the map.
        // If an integer is not in the map, return it as the smallest non-negative integer.
        // If an integer is in the map, decrement its frequency in the map.
        for (int i = 0; i < arrSize; i++) {
            cout << m[i % value] << "\n";
            if (!m[i % value] || m[i % value] == 0) {
                return i;
            } else {
                m[i % value]--;
            }
        } 

        // If all integers from 0 to arrSize-1 are in the map, return arrSize as the smallest non-negative integer.
        return arrSize;
    }
};

```

