class Solution {
    public int findSmallestInteger(int[] nums, int value) {

        int []count=new int[value];

        for(int n:nums){
            int rem=(value + n%value)%value;
            count[rem]++;
        }

        int min=Integer.MAX_VALUE;

        for(int i=0;i<value;i++){
            min=Math.min(min,value*count[i]+i);
        }

        return min;
    }
}
