class Solution {
public:
    int findSmallestInteger(vector<int>& nums, int value) {
        unordered_map<int,int> hm ; 
        for(int &x: nums){
            x = (value + x % value) % value ;
            hm[x]++ ;
            hm[x + value * (hm[x]-1) ] = 1; 
        }

        int i = 0 ; 
        while(true){
            if(hm.count(i)) i++;
            else return i ;
        }
        return -1 ;
    }
};
```

