class Solution {
public:
    int findSmallestInteger(vector<int>& nums, int value) {
        unordered_map <int,int> m;
        for(int i=0;i<nums.size();i++){
           if(nums[i] > 0){
              m[nums[i]%value]++;
           }
           else{
              int k=abs(nums[i])%value;
              if(k==0){
                  m[k]++;
              }
              else{
                 m[value-k]++;
              }
           }
        }
        int ans=INT_MAX;
        if(m.size()==value){
          for(auto &it:m){
             ans=min(ans,(it.second*value)+it.first);
          }
        }
        else{
          for(int i=0;i<value;i++){
             if(m.find(i)==m.end()){
                 ans= i;
                 break;
             } 
          }
        }
        return ans;
    }
};
