class Solution {
public:
    int findSmallestInteger(vector<int>& nums, int value) {
        for(int i=0;i<nums.size();i++){
            if(nums[i]<0){
                nums[i]*=-1;
                int mod=nums[i]%value;
                mod=value-mod;
                nums[i]=mod;
                nums[i]=nums[i]%value;
            }
            else{
                nums[i]=nums[i]%value;
            }
        }
        map<int,int>amap;
        for(int i=0;i<nums.size();i++){
            amap[nums[i]]++;
        }
        int count=0;
        for(auto i: amap){
            if(i.first==count){
                int curr=i.first;
                int k=1;
                while(amap[curr]>1){
                    amap[curr+(k*value)]++;
                    amap[curr]--;
                    k++;
                }
            }
            else{
                continue;
                // return count;
            }
            count++;
        }
        // for(auto i: amap){
            // cout<<i.first<<" "<<i.second<<endl;
        // }
        return count;
    }
};
