class Solution {
    func findSmallestInteger(_ nums: [Int], _ value: Int) -> Int {
        var map = [Int: Int]()
        for num in nums {
            let val = num % value
            let key = val >= 0 ? val : val + value
            map[key, default: 0] += 1
        }
        var ans = 0
        while let cur = map[ans%value], cur > 0 {
            map[ans%value] = cur-1
            ans += 1
        }
        return ans
    }
}
