class Solution:
    def findSmallestInteger(self, nums: List[int], value: int) -> int:
        mod_count = collections.defaultdict(int)
        for num in nums:
            mod_count[num % value] += 1

        missing_min = 0
        for i in range(value):
            if mod_count[i] < mod_count[missing_min]:
                missing_min = i

        return mod_count[missing_min] * value + missing_min
