    int findSmallestInteger(vector<int>& nums, int value) {
        int n = nums.size();
        vector<int> remainders;
        for(int i=0;i<n;i++){
            if(nums[i] < 0) 
                nums[i] += (abs(nums[i])/value + (abs(nums[i])%value != 0)) * value;
            remainders.push_back(nums[i] % value);
        }
        sort(remainders.begin(), remainders.end());
        int mex = 0, mul = 0;
        for(int i=0;i<n;i++){
            if(i > 0 && remainders[i] % value != remainders[i-1] % value) mul = 0;
            remainders[i] += value * mul;
            mul++;
        }
        sort(remainders.begin(), remainders.end());
        for(int i=0;i<n;i++) if(remainders[i] == mex) mex++;
        return mex;
    }
};
