            else if(nums[i] < 0){
                int val = nums[i] * -1;
                val = val / value;
                int mul = (val*value) + nums[i] == 0 ? val : val+1;
                nums[i] = nums[i] + (mul*value);
            }
            else if(nums[i] == value){
              nums[i] = 0;
            }
            //Duplicate Checks & Multiply Based On The Count
            if(!set.add(nums[i])){
                int count = map.get(nums[i]);
                map.put(nums[i], ++count);
                nums[i] = nums[i]+(count*value);
                continue;
            }
            else
                map.put(nums[i], 0);
        }
        //Sort to check the 1st missing number
        Arrays.sort(nums);
        int min = -1;
        if(nums[0] != 0)
          return 0;
        //Removes if still having duplicates
        for(int i=0; i<nums.length; i++){
            if(nums[i] != min+1)
              return min+1;
            if(i != nums.length-1)
                min += nums[i] != nums[i+1] ? 1 : 0; 
        }
        return nums[nums.length-1]+1;
    }
}
