class Solution {
public:
    int findSmallestInteger(vector<int>& nums, int value) {
        map<int,int>mp;
        for(int i=0;i<nums.size();i++){
            int t=nums[i]%value;
            if(t<0){
                t=t+value;
            }
            mp[t]++;
        }
        int ans=0;
        while(true){
            if(mp[ans]==0){
                return ans;
            }
            mp[ans]--;
            int count=mp[ans];
            while(count){
                int next=ans +(count*value);
                count--;
                mp[next]++;
            }
            ans++;
        }
        return (-1);
    }
};
```

