var findSmallestInteger = function(nums, value) {
    nums = nums.map(num => {
        const val = num % value;
        if (val < 0) return val + value
        return val;
    });

    const freq = {};

    for (const num of nums) {
        if (freq[num]) freq[num]++;
        else freq[num] = 1;
    }

    let cur = 0;
    let res = Infinity;

    while(cur < value) {
        if (freq[cur] === undefined) return cur;
        let missing = (freq[cur] * value) + cur;
        res = Math.min(res, missing);
        cur++;
    }

    return res;
};




