class Solution {
    public int findSmallestInteger(int[] nums, int value) {
        int size = nums.length;
        Map<Integer,Integer> remCount = new HashMap<>();
        for(int num : nums){
            int rem = num % value;
            if(num < 0 && rem != 0){
                num = -num;
                int q = num / value;
                rem = (-num) + (value*(q+1));
            }
            int val = remCount.getOrDefault(rem,0);
            remCount.put(rem,val+1);
        }
        for(int i = 0; i < size; i++){
            int key = i % value;
            if(remCount.getOrDefault(key,0) == 0){
                return i;
            }else{
                int val = remCount.get(key);
                remCount.put(key,val-1);
            }
        }
        return size;
    }
}
