class Solution {
public:
    int findSmallestInteger(vector<int>& a, int k) {
      int n = a.size();
      vector<int> dp(k, 0);
      for(int i=0; i<n; i++){
        if(a[i]%k >= 0){
          dp[a[i]%k]++;
        }
        else{
          dp[k+a[i]%k]++;
        }
      }
      for(int i=0; i<n; i++){
        if(!dp[i%k]){
          return i;
        }
        else{
          dp[i%k]--;
        }
      }
      return n;
    }
};
