class Solution {
public:
    int findSmallestInteger(vector<int>& nums, int value) {
        map<int,int>mp1;
        for(auto it : nums){
            if(it%value<0){
                mp1[value + it%value]++;
            }
            else{
                mp1[it%value]++;
            }
        }

        int N = 1e5+1;
        for(int i=0;i<N;i++){
            if(mp1[i]!=0){
                mp1[i]--;
            }
            else if(mp1[i%value]!=0){
                mp1[i%value]--;
            }

            else{
                return i;
            }
        }
        return 0;
    }
};
