/**
 * @param {any} obj
 * @param {any} classFunction
 * @return {boolean}
 */

// https://developer.mozilla.org/en-US/docs/Learn/JavaScript/Objects/Object_prototypes
// https://developer.mozilla.org/en-US/docs/Web/JavaScript/Reference/Operators/instanceof

var checkIfInstanceOf = function(obj, classFunction) {
    if (obj === null | obj === undefined || typeof classFunction !== 'function') return false;

    const objPrototype = Object.getPrototypeOf(obj);
    const doesMatchPrototype =
        objPrototype === classFunction?.prototype;
    const doesMatchInstance =
        objPrototype instanceof classFunction;

    return doesMatchPrototype || doesMatchInstance;
};

/**
 * checkIfInstanceOf(new Date(), Date); // true
 */
```

