/**
 * @param {any} obj
 * @param {any} classFunction
 * @return {boolean}
 */
var checkIfInstanceOf = function(obj, classFunction) {
    // Check if obj or classFunction is null
    if (obj === null || classFunction === null) {
        return false;
    }

    // Check if both obj and classFunction are defined
    if (typeof obj === 'undefined' || typeof classFunction === 'undefined') {
        return false;
    }

    // Handle primitive values and instances of built-in types
    if ((typeof obj === 'number' && (classFunction === Number || classFunction === Object)) ||
        (typeof obj === 'string' && (classFunction === String || classFunction === Object)) ||
        (typeof obj === 'boolean' && (classFunction === Boolean || classFunction === Object)) ||
        (typeof obj === 'bigint' && (classFunction === BigInt || classFunction === Object)) ||
        (typeof obj === 'symbol' && (classFunction === Symbol || classFunction === Object)) ||
        (obj.constructor === classFunction)) {
        return true;
    }

    // Additional check for NaN as an instance of Object
    if (Number.isNaN(obj) && classFunction === Object) {
        return true;
    }

    // Check if obj is an instance of classFunction using instanceof operator
    // Ensure classFunction is a function before using instanceof
    return typeof classFunction === 'function' && obj instanceof classFunction;
};








