/**
 * @param {any} obj
 * @param {any} classFunction
 * @return {boolean}
 */
var checkIfInstanceOf = function(obj, classFunction) {
    if (typeof obj === 'undefined' || typeof classFunction === 'undefined' || classFunction === null) {
        return false;
    }
    if (obj === null) {
        return false;
    }

    var classPrototype = classFunction.prototype;

    while (obj !== null) {
        if (obj === classPrototype) {
            return true;
        }
        obj = Object.getPrototypeOf(obj);
    }
    return false;
};

