        if(n==0) {
            return false;
        }
        // remove all 2's factors
        while(n%2==0) {
            n = n/2;
        }

        // remove all 3's factors
        while(n%3==0) {
            n = n/3;
        }

        // remove all 5's factors
        while(n%5==0) {
            n = n/5;
        }

 // now if n became 1 means all factors were only combinations of 2,3,5 which have been remove by above process
        if(n==1) {
            return true;
        }
        // else return false because it has other factors than 2 ,3 and 5
        return false;
    }
};

