        if(n <= 0){
            return false;
        }
        while((n & 1) != 1){
            n = n/2;
        }
        while(n%3 == 0){
            n = n/3;
        }
        while(n%5 == 0){
            n = n/5;
        }
        if(n == 1){
            return true;
        }else{
            return false;
        }
    }
}
