class Solution {
public:
    bool sohel(int n)
    {
        return n == 2 or n == 3 or n == 5 or n == 1;
    }
    bool isUgly(int n) {
       // cout << INT_MIN << endl;
       n = (long long)n;
       vector<bool>prime(n+1 , true);
       long long i , j;
       for(i = 2 ; i*i <= n ; i++)
       {
           if(prime[i])
           {
               for(j = i*i ; j <= n ; j+=i)
               prime[j] = false;
           }
       }
        for(i = 2 ; i*i <= n ; i++)
        {
            if(prime[i])
            {
                while(n%i == 0)
                {
                    if(!sohel(i))
                    return false;
                    n /= i;
                }
            }
        }
        return sohel(n);
    }
};
