class Solution {
public:
bool isNumber(string s) {
    int begin=0, end=s.size()-1;
    while(s[begin]==' '&&++begin<s.size());
    while(s[end]==' '&&--end>=0); 
    if(s[begin]=='-'||s[begin]=='+') begin++;
    if(begin>end) return false;
    bool isdot = false, ise = false;
    for(int i = begin; i<=end; i++){
        if(s[i]=='.'){
            if(begin==end) return false;
            if(s[i-1]=='e') return false;
            if(isdot||ise) return false;
            else isdot = true;
        }
        else if(s[i]=='e'){
            if(i==begin||i==end) return false; 
            if(s[i-1]=='.'&&i-1==begin) return false;
            if(ise) return false;
            else ise = true;
        }
        else if(s[i]>='0'&&s[i]<='9') continue;
        else if(s[i]=='-'||s[i]=='+'){
            if(s[i-1]!='e'||i==end) return false;
        }
        else return false;
    }
    return true;
}
};

