class Solution:
    def isNumber(self, s: str) -> bool:
        D = 'digit'
        dfa = {
            0: {
                '+': 1,
                '-': 1,
                D: 2,
                '.':5
            },
            1: {
                D: 2,
                '.':5
            },
            2: {
                '.':3,
                D: 2,
                'e':6,
                'E':6
            },
            3: {
                D:4,
                'e':6,
                'E':6
            },
            4: {
                D:4,
                'e':6,
                'E':6
            },
            5: {
                D: 4
            },
            6: {
                '+': 7,
                '-': 7,
                D: 8
            },
            7: {
                D: 8
            },  
            8: {
                D: 8
            },  
        }

        accepted_states = (2, 3, 4, 8)
        state = 0
        for s_ in s:
            key = s_ if not s_.isdigit() else 'digit'
            if key not in dfa[state]:
                return False
            state = dfa[state][key]
        return state in accepted_states
