bool is_valid_number(const std::string& s) {
    if (s.empty()) return false;

    size_t i = 0;
    if (s[i] == '+' || s[i] == '-') i++;

    bool has_integer_part = false;
    while (i < s.size() && isdigit(s[i])) {
        has_integer_part = true;
        i++;
    }

    bool has_decimal_part = false;
    if (i < s.size() && s[i] == '.') {
        i++;
        while (i < s.size() && isdigit(s[i])) {
            has_decimal_part = true;
            i++;
