class Solution(object):
    def isNumber(self, s):
        """
        :type s: str
        :rtype: bool
        """
        if not s:
            return False
        s = s.strip()
        i, n = 0, len(s)
        num, dot, exp, e_idx = False, False, False, -1
        while i < n:
            if s[i].isdigit():
                num = True
            elif s[i] == '.':
                if dot or exp:
                    return False
                dot = True
            elif s[i] == 'e':
                if exp or not num:
                    return False
                exp = True
                num = False
                e_idx = i
            elif s[i] in ['+', '-']:
                if i != 0 and i != e_idx+1:
                    return False
            else:
                return False
            i += 1
        return num

    def isNumberRegex(self, s):
        if not s:
            return False
        p =  r'^\s*[-+]?(\d+(\.\d*)?|\.\d+)(e[-+]?[0-9]+)?\s*$'
        m = re.match(p, s)
