class Solution {
public:
    bool isNumber(string s) {
        int i=0, n=s.size();
        /** skip space **/
        while(s[i]==' ') i++;
        /** get sign **/
        if(s[i]=='+' || s[i]=='-')  i++;
        /** check number and points **/
        int c1=0;
        while(i<n && s[i]>='0' && s[i]<='9') { i++;  c1++; }
        if(i<n && s[i]=='.')  i++;
        while(i<n && s[i]>='0' && s[i]<='9') { i++;  c1++; }
        if(c1==0)  return false;
        /** check exponent parts **/
        if(s[i]=='e') {
            i++;
            if(s[i]=='+' || s[i]=='-')  i++;
            c1=0;
            while(s[i]>='0' && s[i]<='9')  { i++; c1++; }
            if(c1<1)  return false;
        }
        /** skip space **/
        while(s[i]==' ') i++;
        return i==n;
    }
};

