```
    /**
     * Given a string, return true if it is a valid number.
     * Time Complexity: O(n).
     * Space Complexity: O(1).
     *
     * @param s A string consists of only English letters (both uppercase and lowercase),
     *          digits (0-9), plus '+', minus '-', or dot '.'
     * @return true if the given string is a valid number, false otherwise.
     */
    public boolean isValid(String s) {
        boolean hasDigit = false;
        boolean hasExponent = false;
        boolean hasDecimal = false;
        boolean needDigitAfterExponent = false;

        s = s.trim();

        for (int i = 0; i < s.length(); i++) {
            char c = s.charAt(i);
            if (c == '+' || c == '-') {
                if (i != 0 && s.charAt(i - 1) != 'E' && s.charAt(i - 1) != 'e') return false;

            } else if (Character.isDigit(c)) {
                hasDigit = true;
                needDigitAfterExponent = false;

            } else if (c == '.') {
                if (hasDecimal || hasExponent) return false;
                hasDecimal = true;

            } else if (c == 'E' || c == 'e') {
                if (hasExponent || !hasDigit) return false;
                hasExponent = true;
                needDigitAfterExponent = true;

            } else return false;
        }

        return !needDigitAfterExponent && hasDigit;
    }
