class Solution:
    def compareVersion(self, version1: str, version2: str) -> int:
        if len(version1) <= 3 and len(version2) <= 3:
            if float(version1) > float(version2):
                return 1
            elif float(version1) < float(version2):
                return -1
            else:
                return 0

        u = version1.split('.')
        v = version2.split('.')

        if len(u) > len(v):
            v += ['0'] * (len(u) - len(v))
        elif len(v) > len(u):
            u += ['0'] * (len(v) - len(u))

        for i in range(min(len(u), len(v))):
            if int(u[i]) > int(v[i]):
                return 1
            elif int(u[i]) < int(v[i]):
                return -1
        return 0
