class Solution {
public:
    int compareVersion(string version1, string version2) {
	    // Load version strings into streams.
        stringstream s1(version1), s2(version2);
		// Two vectors to store the version numbers.
        vector<int> v1, v2;
        string temp;
		// Extract elementwise from stream using '.' as delimiter.
        while(getline(s1, temp, '.'))
            v1.push_back(stoi(temp));
        while(getline(s2, temp, '.'))
            v2.push_back(stoi(temp));
        int i;
		// Compare the vectors until we run out of elements in either.
        for (i = 0; i < v1.size() && i < v2.size(); ++i){
            if (v1[i] < v2[i]) return -1;
            if (v1[i] > v2[i]) return 1;
        }
		// If the longer version string has a non-zero element after common elements, it is greater.
        for(;i < v1.size();++i)
            if (v1[i]) return 1;
        for(;i < v2.size();++i)
            if (v2[i]) return -1;
		// The versions are equal if the code has gotten this far.
        return 0;
    }
};
