function sumOfMultiples(n) {
    // Sum of numbers divisible by 3 in range [0, n]
    const sum3 = mulSum(n, 3);
    // Sum of numbers divisible by 5 in range [0, n]
    const sum5 = mulSum(n, 5);
    // Sum of numbers divisible by 7 in range [0, n]
    const sum7 = mulSum(n, 7);

    // Sum of numbers divisible by 3 and 5 in range [0, n]
    const sum15 = mulSum(n, 3 * 5);
    // Sum of numbers divisible by 3 and 7 in range [0, n]
    const sum21 = mulSum(n, 3 * 7);
    // Sum of numbers divisible by 5 and 7 in range [0, n]
    const sum35 = mulSum(n, 5 * 7);

    // Sum of numbers divisible by 3, 5 and 7 in range [0, n]
    const sum105 = mulSum(n, 3 * 5 * 7);

    return sum3 + sum5 + sum7 - sum15 - sum21 - sum35 + sum105;
};

// Computes sum of numbers divisible by x in range [0, n]
function mulSum(n, x) {
    const cnt = Math.floor(n / x);
    return cnt * (cnt * x + x) / 2;
}
```

