class Solution:
    def isWinner(self, player1: List[int], player2: List[int]) -> int:
        Stack_A = []
        stack_B = []
        aScore = 0
        bScore = 0
        for n,m in zip(player1,player2):

            if 10 in Stack_A[-2:]:
                aScore += 2*n
            else:
                aScore += n

            if 10 in stack_B[-2:]:
                bScore += 2*m
            else:
                bScore += m
            Stack_A.append(n)
            stack_B.append(m)

        if aScore == bScore:return 0
        return 1 if aScore > bScore else 2

