class Solution:
    def isWinner(self, player_a: List[int], player_b: List[int]) -> int:
        score_a = self.get_score(player_a)
        score_b = self.get_score(player_b)

        return {
            score_a > score_b: 1,
            score_a < score_b: 2,
        }.get(True, 0)

