    def get_score(player: List[int]) -> int:
        score = 0

        for i, sc in enumerate(player):
            if i > 0 and player[i - 1] == 10:
                score += 2 * sc
            elif i > 1 and player[i - 2] == 10:
                score += 2 * sc
            else:
                score += sc

        return score

