class Solution {
public:
    int gameResult(const vector<int>& player) {
        int hit = 0, sum = 0;
        for(auto pins : player) {
            sum += pins;
            if(hit) { sum += pins; --hit; }
            if(pins == 10) hit = 2;
        }
        return sum;
    }

    int isWinner(const vector<int>& player1, const vector<int>& player2) {
        int result = gameResult(player1) - gameResult(player2);
        return result > 0? 1: result < 0? 2: result;
    }
};
