class Solution {
public:
    int getScore(vector<int> player){
        bool flag=false;
        int previousTwo = 0;
        int totalScore=0;
        for(auto&it:player){
            if(flag==false){
                totalScore+=it;
            }else if(previousTwo<2){
                totalScore+= (2*it);
                previousTwo+=1;
            }
            if(previousTwo>=2)flag=false;
            if(it==10){
                flag=true;
                previousTwo=0;
            }
        }
        return totalScore;
    }
    int isWinner(vector<int>& player1, vector<int>& player2) {
        int player1Score = getScore(player1);
        int player2Score = getScore(player2);
        if(player1Score>player2Score){
            return 1;
        }else if(player1Score<player2Score){
            return 2;
        }else{
            return 0;
        }
    }
};
