class Solution:
    def isWinner(self, player1: List[int], player2: List[int]) -> int:

        def get_score(scores)-> int:
            total = scores[0]

            if len(scores) > 1:
                if total == 10:
                    total += (2 * scores[1])
                else:
                    total += scores[1]

            # Start at index 2
            for i in range(2, len(scores)):
                if scores[i - 1] == 10 or scores[i - 2] == 10:
                    total += (2 * scores[i])
                else:
                    total += scores[i]
            return total

        p1 = get_score(player1)
        p2 = get_score(player2)

        if p1 == p2:
            return 0
        elif p1 > p2:
            return 1
        else:
            return 2




