public class Solution {
    public int IsWinner(int[] player1, int[] player2) {
        int Calc(int[] input) {
            var sum = 0;
            bool HitTenAt(int pos) {
                if (pos < 0) {
                    return false;
                }
                if (pos >= input.Length) {
                    return false;
                }
                return input[pos] == 10;
            }

            for (var i = 0; i < input.Length; i++) {
                if (HitTenAt(i-1) || HitTenAt(i-2)) {
                    sum += input[i]*2;
                } else {
                    sum += input[i];
                }
            }

            return sum;
        }

        var one = Calc(player1);
        var two = Calc(player2);
        if  (one == two) {
            return 0;
        }

        return one > two ? 1 : 2;
    }
}
