class Solution {
    func isWinner(_ player1: [Int], _ player2: [Int]) -> Int {
        var player1HasStrike = false
        var player2HasStrike = false
        var scoresOf1Player = 0
        var scoresOf2Player = 0
        var counter1 = 0
        var counter2 = 0

        for (int1, int2) in zip(player1, player2) {
            if player1HasStrike {
                scoresOf1Player += int1 * 2
            } else {
                scoresOf1Player += int1
            }

            if player2HasStrike {
            scoresOf2Player += int2 * 2
            } else {
                scoresOf2Player += int2
            }

            if int1 == 10 {
                player1HasStrike = true
                counter1 = 0
            }

            if int2 == 10 {
                player2HasStrike = true
                counter2 = 0
            }

            if counter1 == 2, player1HasStrike {
                player1HasStrike = false
            }

            if counter2 == 2, player2HasStrike {
                player2HasStrike = false
            }

            counter1 += 1
            counter2 += 1
        }

        if scoresOf1Player == scoresOf2Player {
            return 0
        }

        return scoresOf1Player > scoresOf2Player ? 1 : 2
    }
}
