        int result = 0;
        int countStrike = 0;
        for (int num : player) {
            result += num;
            if (countStrike != 0) {
                result += num;
                countStrike--;
            }
            if (num == 10) {
                countStrike = 2;
            }
        }
        return result;
    }
}
