public class Solution {
    public int IsWinner(int[] player1, int[] player2) 
    {
        var diff = GetScore(player1) - GetScore(player2);

        return diff == 0 ? 0 : (diff > 0 ? 1 : 2);
    }

    int GetScore(int[] hits)
    {
        var doubling = 0;

        var score = 0;

        foreach(var h in hits)
        {
            score += (doubling-- > 0) ? 2 * h : h;

            if(h == 10) doubling = 2;
        }

        return score;
    }
}
