class Solution {
public:
    int isWinner(vector<int>& player1, vector<int>& player2) {
        int a = player1.size(), b = player2.size();

        int first = 0, second = 0, count = 0, i = 0;
        while (i < a) {
            if (player1[i] == 10) {
                count = 2;
                first += player1[i];
                i++;
                while (count != 0 && i < a) {
                    first += (2 * player1[i]);
                    count--;
                    if (player1[i] == 10) count = 2;
                    i++;
                }
            }
            else {
                first += player1[i];
                i++;
            }
        }

        i = 0;
        count = 0;
        while (i < b) {
            if (player2[i] == 10) {
                count = 2;
                second += player2[i];
                i++;
                while (count != 0 && i < b) {
                    second += (2 * player2[i]);
                    count--;
                    if (player2[i] == 10) count = 2;
                    i++;
                }
            }
            else {
                second += player2[i];
                i++;
            }
        }

        if (first > second) return 1;
        else if (first < second) return 2;
        else return 0;
    }
};

