use std::cmp::Ordering::{Greater, Less, Equal};

impl Solution {
    pub fn is_winner(player1: Vec<i32>, player2: Vec<i32>) -> i32 {
       let score1 = Solution::score(player1);
       let score2 = Solution::score(player2);

       match score1.cmp(&score2) {
            Greater => 1,
            Less => 2,
            Equal => 0,
        }
    }

    fn score(player: Vec<i32>) -> i32 {
        player.into_iter().fold((0, 0, 0), |(n2, n1, score), x| {
            if n2 == 10 || n1 == 10 {
                (n1, x, score + 2 * x)
            } else {
                (n1, x, score + x)
            }
        }).2
    }
}
